TRANSFORMFIELD.EXPRESSION:
IIF(
            (  P_DT_COMPETENCIA_VAR  < SYSDATE AND
              TRUNC ( P_DT_COMPETENCIA_VAR, 'MM') = TRUNC ( SYSDATE, 'MM' ) 
           )    OR 
               ( P_DT_COMPETENCIA_VAR > SYSDATE  AND
               TRUNC ( P_DT_COMPETENCIA_VAR, 'MM') <> TRUNC ( SYSDATE, 'MM' )
            ) ,
     ADD_TO_DATE( P_DT_COMPETENCIA_VAR, 'MM', 1) , 
    TRUNC ( SYSDATE ) 
      )

IIF(
            (  P_DT_COMPETENCIA_VAR  < SYSDATE AND
              TRUNC ( P_DT_COMPETENCIA_VAR, 'MM') = TRUNC ( SYSDATE, 'MM' ) 
           )    OR 
               ( P_DT_COMPETENCIA_VAR > SYSDATE  AND
               TRUNC ( P_DT_COMPETENCIA_VAR, 'MM') <> TRUNC ( SYSDATE, 'MM' )
            ) ,
     ADD_TO_DATE( P_DT_COMPETENCIA_VAR, 'MM', 1) , 
    TRUNC ( SYSDATE ) 
      )

IIF(
            (  DT_AGENDA < SYSDATE AND
              TRUNC ( DT_AGENDA, 'MM') = TRUNC ( SYSDATE, 'MM' ) 
           )    OR 
               ( DT_AGENDA > SYSDATE  AND
               TRUNC ( DT_AGENDA, 'MM') <> TRUNC ( SYSDATE, 'MM' )
            ) ,
     ADD_TO_DATE(  DT_AGENDA, 'MM', 1) , 
    TRUNC ( SYSDATE ) 
      )

IIF(
            (  DT_AGENDA < SYSDATE AND
              TRUNC ( DT_AGENDA, 'MM') = TRUNC ( SYSDATE, 'MM' ) 
           )    OR 
               ( DT_AGENDA > SYSDATE  AND
               TRUNC ( DT_AGENDA, 'MM') <> TRUNC ( SYSDATE, 'MM' )
            ) ,
     ADD_TO_DATE(  DT_AGENDA, 'MM', 1) , 
    TRUNC ( SYSDATE ) 
      )
